import React, { useEffect, useState, useRef } from 'react';
import { io } from 'socket.io-client';

const socket = io();

function Chat({ userData }) {
  const [partner, setPartner] = useState(null);
  const [messages, setMessages] = useState([]);
  const [message, setMessage] = useState('');
  const messagesEndRef = useRef(null);

  useEffect(() => {
    socket.emit('join', userData);

    socket.on('partner', (partnerInfo) => {
      setPartner(partnerInfo);
      setMessages([{ sender: 'System', text: `You’re now chatting with ${partnerInfo.nickname}` }]);
    });

    socket.on('message', (msg) => {
      setMessages((prev) => [...prev, msg]);
    });

    socket.on('partnerDisconnected', () => {
      setMessages((prev) => [...prev, { sender: 'System', text: 'Stranger disconnected.' }]);
      setPartner(null);
    });

    return () => {
      socket.disconnect();
    };
  }, [userData]);

  const sendMessage = () => {
    if (message.trim()) {
      const msg = {
        sender: userData.nickname,
        avatar: userData.avatar,
        text: message
      };
      socket.emit('message', msg);
      setMessages((prev) => [...prev, msg]);
      setMessage('');
    }
  };

  const skipPartner = () => {
    socket.emit('skip');
    setPartner(null);
    setMessages([{ sender: 'System', text: 'Searching for a new stranger...' }]);
  };

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  return (
    <div className="chat-container">
      <div className="chat-header">
        <span>{userData.avatar} {userData.nickname} (You)</span>
        {partner && <span>Talking to: {partner.avatar} {partner.nickname}</span>}
        <button onClick={skipPartner}>Skip</button>
      </div>

      <div className="chat-box">
        {messages.map((msg, i) => (
          <div key={i} className="message">
            <strong>{msg.sender === userData.nickname ? 'You' : `${msg.avatar} ${msg.sender}`}:</strong> {msg.text}
          </div>
        ))}
        <div ref={messagesEndRef} />
      </div>

      <div className="input-area">
        <input
          type="text"
          placeholder="Type your message..."
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          onKeyDown={(e) => e.key === 'Enter' && sendMessage()}
        />
        <button onClick={sendMessage}>Send</button>
      </div>
    </div>
  );
}

export default Chat;
