import React, { useEffect, useRef, useState } from "react";
import { io } from "socket.io-client";
import EmojiPicker from "emoji-picker-react";

const socket = io();

function Chat({ nickname }) {
  const [messages, setMessages] = useState([]);
  const [message, setMessage] = useState("");
  const [showEmojiPicker, setShowEmojiPicker] = useState(false);
  const [partnerConnected, setPartnerConnected] = useState(false);
  const messagesEndRef = useRef();

  useEffect(() => {
    socket.emit("join", nickname);

    socket.on("message", (msg) => {
      setMessages((prev) => [...prev, msg]);
    });

    socket.on("partnerDisconnected", () => {
      setMessages((prev) => [...prev, { from: "system", text: "Stranger left." }]);
      setPartnerConnected(false);
    });

    socket.on("partnerFound", () => {
      setMessages([{ from: "system", text: "Stranger connected!" }]);
      setPartnerConnected(true);
    });

    return () => socket.disconnect();
  }, [nickname]);

  const sendMessage = () => {
    if (message.trim() !== "") {
      socket.emit("message", message);
      setMessages((prev) => [...prev, { from: "you", text: message }]);
      setMessage("");
    }
  };

  const handleEmojiClick = (emojiData) => {
    setMessage((prev) => prev + emojiData.emoji);
  };

  const handleSkip = () => {
    socket.emit("skip");
    setMessages([]);
    setPartnerConnected(false);
  };

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  return (
    <div className="chat-container">
      <div className="chat-header">
        <span>{nickname}</span>
        <button onClick={handleSkip}>Skip</button>
      </div>
      <div className="messages">
        {messages.map((m, i) => (
          <div key={i} className={`message ${m.from}`}>
            <b>{m.from === "you" ? "You" : m.from === "system" ? "System" : "Stranger"}:</b> {m.text}
          </div>
        ))}
        <div ref={messagesEndRef} />
      </div>
      <div className="chat-input">
        <button onClick={() => setShowEmojiPicker(!showEmojiPicker)}>ðŸ˜Š</button>
        {showEmojiPicker && <EmojiPicker onEmojiClick={handleEmojiClick} />}
        <input
          type="text"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          onKeyDown={(e) => e.key === "Enter" && sendMessage()}
          placeholder="Type your message..."
        />
        <button onClick={sendMessage}>Send</button>
      </div>
    </div>
  );
}

export default Chat;
